            error = result.error or "Berry activation failed",
            correlation = { id = correlationId, responseType = "ACTIVATION_PROCESS_ERROR" }
        }
    end
    
    return {
        success = true,
        correlation = { id = correlationId, responseType = "ACTIVATION_PROCESS_SUCCESS" },
